import React from "react";
import Search from "./Search.js";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = { onClick: props.onClick, showQuery: false };
  }

  render() {
    const { onClick, showQuery } = this.state;
    let res;
    if (showQuery) {
      res = (
        <div className="container-form">
          <form>
            <Search header={"University"}></Search>
            <Search header={"Department"}></Search>
            <Search header={"Professor"}></Search>
            <button onClick={onClick} className="huge ui button">
              Search
            </button>
          </form>
        </div>
      );
    }
    return res;
  }
}

export default Form;
