import React from "react";
import Search from "./Search.js";
import { tsThisType } from "@babel/types";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      onClick: props.onClick,
      formSize: props.formSize,
      messages: [true, true, true]
    };
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentDidUpdate(prevProps) {
    if (this.props.formSize !== prevProps.formSize) {
      this.setState({ formSize: this.props.formSize });
    }
  }

  onSubmit(e) {
    e.preventDefault();
    const { messages } = this.state;
    const data = new FormData(e.target);
    if (data.get("university") === null) {
      messages[0] = false;
    }
    if (data.get("department") === null) {
      messages[1] = false;
    }
    if (data.get("professor") === null) {
      messages[2] = false;
    }
    let moveDown = true;
    for (let mess of messages) if (!mess) moveDown = false;
    if (!moveDown) this.setState({ messages });
    else this.state.onClick();
  }

  render() {
    const { onClick, formSize } = this.state;

    return (
      <div className="container-form" style={{ top: formSize }}>
        <form onSubmit={this.onSubmit}>
          <Search header={"University"} name={"university"}></Search>
          <Search header={"Department"} name={"department"}></Search>
          <Search header={"Professor"} name={"professor"}></Search>
          <button className="huge ui button">Search</button>
        </form>
      </div>
    );
  }
}

export default Form;
