import React from "react";
import Search from "./Search.js";

class Form extends React.Component {
  state = {
    containerSize: "100%"
  };

  onClick(e) {
    e.preventDefault();
    this.setState({ containerSize: "200%" });
  }

  render() {
    const { containerSize } = this.state;

    return (
      <div className="container-form" style={{ height: containerSize }}>
        <form>
          <Search header={"University"}></Search>
          <Search header={"Department"}></Search>
          <Search header={"Professor"}></Search>
          <button onClick={this.onClick} className="huge ui button">
            Search
          </button>
        </form>
      </div>
    );
  }
}

export default Form;
