import React from "react";
import Search from "./Search.js";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = { onClick: props.onClick, formSize: props.formSize };
  }

  componentDidUpdate(prevProps) {
    if (this.props.formSize !== prevProps.formSize) {
      this.setState({ formSize: this.props.formSize });
    }
  }

  onSubmit(e) {
    e.preventDefault();
    const data = new FormData(e.target);
    const university = data.get("university");
    const department = data.get("department");
    const professor = data.get("professor");
    console.log(university);
  }

  render() {
    const { onClick, formSize } = this.state;

    return (
      <div className="container-form" style={{ top: formSize }}>
        <form onSubmit={this.onSubmit}>
          <Search header={"University"} name={"university"}></Search>
          <Search header={"Department"} name={"department"}></Search>
          <Search header={"Professor"} name={"professor"}></Search>
          <button onClick={onClick} className="huge ui button">
            Search
          </button>
        </form>
      </div>
    );
  }
}

export default Form;
